<?php
session_start();
include '../include/config.php'; // Include your database connection file

// Ensure $transaction_id is defined and sanitize input
$transaction_id = isset($_GET['transaction_id']) ? intval($_GET['transaction_id']) : null; 
if (!$transaction_id) {
    // Handle missing transaction ID
    header("Location: error.php?error=Transaction ID is required.");
    exit();
}

try {
    // Fetch the user_id and document_type from the transactions table using the transaction ID
    $query = "SELECT user_id, document_type, other_document_type, purpose, board, term, SY, status_type, year, other_status 
              FROM transactions WHERE id = ?";
    $stmt = $conn->prepare($query);
    if (!$stmt) {
        throw new Exception("Error preparing statement: " . $conn->error);
    }
    $stmt->bind_param('i', $transaction_id);
    $stmt->execute();
    $stmt->bind_result($user_id, $document_type, $other_document_type, $purpose, $board, $term, $SY, $status_type, $year, $other_status);
    $stmt->fetch();
    $stmt->close();

    // Check if user_id is fetched
    if (!$user_id) {
        // Handle transaction not found
        header("Location: error.php?error=Transaction not found.");
        exit();
    }

    // Process document_type array
    $document_types_array = explode(',', $document_type); // Exploding by comma only
    $document_types_array = array_map('trim', $document_types_array); // Trim whitespace

    

    // Fetch the firstname, lastname, and slot_date in a single query based on user_id and transaction_id
    $sql = "SELECT u.firstname, u.lastname, b.slot_date 
            FROM user u 
            JOIN bookings b ON b.id = (SELECT booking_id FROM transactions WHERE id = ?)
            WHERE u.id = ?";
    $stmt = $conn->prepare($sql);
    if (!$stmt) {
        throw new Exception("Error preparing statement: " . $conn->error);
    }
    $stmt->bind_param("ii", $transaction_id, $user_id);
    $stmt->execute();
    $stmt->bind_result($firstname, $lastname, $slot_date);
    $stmt->fetch();
    $stmt->close();

    // Check if firstname and lastname are fetched
    if (!$firstname || !$lastname) {
        // Handle user not found
        header("Location: error.php?error=User not found.");
        exit();
    }

} catch (Exception $e) {
    // Log the exception or redirect to an error page
    header("Location: error.php?error=" . urlencode($e->getMessage()));
    exit();
} finally {
    // Close the database connection
    $conn->close();
}
?>






